import React from 'react';
import './Address.css'

/**
 * http://microformats.org/wiki/hcard-ru
 * @param line1
 * @param line2
 * @param zip
 * @param phone
 * @param name
 * @param email
 * @returns {*}
 */
export default ({ line1, line2 = '', zip, phone, name, email }) => (
    <div className="vcard">
      <strong className="n">{name}</strong>
      <div className="adr">
        {`Адрес: ${zip}, ${line1} ${line2}`}
      </div>
      <div className="tel">
        {`Телефон: ${phone}`}
      </div>
      <div className="email">
        {`E-mail: ${email}`}
      </div>

      <br/>

    </div>
)

/**
 *
 * @param address
 * @returns {*}
 */
export const parse = (address) => {
  if (address.manualInput) {
    return {
      line1: [address.country, address.region, address.city].join(','),
      line2: [address.street, address.house, address.room].join(','),
      name: address.name,
      phone: address.phone,
      email: address.email,
      zip: address.postalCode,
    }
  }

  return {
    line1: address.address,
    name: address.name,
    email: address.email,
    phone: address.phone,
    zip: address.postalCode,
  }
};