import React from 'react';
import cx from 'classnames';
import './BtnPlusAndMinus.css';

export default ({ onAdd, onRemove, index = 0, dataSource = [], limit = Infinity }) => {
  const isAdd = index === 0;
  const isAllowAdd = limit > dataSource.length;

  if (isAdd && isAllowAdd) {
    return (
      <div className={'BtnPlusAndMinus'}>
        <div className={cx('BtnPlusAndMinus__btn')} onClick={() => onAdd()}>
          +
        </div>
      </div>
    );
  } else if (!isAdd) {
    return (
      <div className={'BtnPlusAndMinus'}>
        <div className={cx('BtnPlusAndMinus__btn')} onClick={() => onRemove()}>
          -
        </div>
      </div>
    );
  }

  return <div />;
};