import React from 'react';
import cx from 'classnames';
import './Button.css';

const noop = () => {
};
export default ({onClick, disabled, style, appear, locked, lock, className, children}) => (
    <div onClick={disabled ? noop : onClick}
         className={cx('Button', className, {
           'disabled': disabled,
           'locked': locked,
           'Button--dark': style === 'dark',
           'Button--appear-form': appear === 'form',
         })}>{locked ? (lock || children) : children}</div>
)
