import React from 'react';
import cx from 'classnames';
import './Button.css';

const noop = () => {
};
export default ({onClick, disabled, color, size, appear, locked, lock="Обработка", className, children, style = {}}) => (
    <div onClick={disabled ? noop : onClick}
         style={style}
         className={cx('Button', className, {
           'disabled': disabled,
           'locked': locked,
           'Button--link': appear === 'link',
           'Button--dark': color === 'dark',
           'Button--size-md': size === 'md',
           'Button--size-small': size === 'small',
         })}>{locked ? (lock || children) : children}</div>
)
