import React from 'react';
import cx from 'classnames';
import {noop, uniqueId} from 'underscore';
import './Checkbox.css';

export default ({
                  label, className, color, name = uniqueId(
      'check'), checked, onChange = noop,
                }) => (
    <div className={cx('Checkbox')}>
      <div id={name}
           name={name}
           className={cx('Checkbox__indicator', {
             'Checkbox__indicator--black': color === 'black',
             'Checkbox__indicator--checked': checked,
           })}
           onClick={(e) => {
             e.preventDefault();
             e.stopPropagation();
             onChange(!checked);
           }}
      />
      {label &&
      <label className={cx('Checkbox__label', 'form-check-label')}
             htmlFor={name}
             onClick={(e) => {
               e.preventDefault();
               e.stopPropagation();
               onChange(!checked);
             }}>{label}</label>}
    </div>
)