import React from 'react';
import GeoTextField from 'components/GeoTextField';
import { handleChange } from './helper';

export default ({ value, onChange=()=>{}, onRequest=()=>{}, ...rest }) => (
  <GeoTextField
    {...rest}
    value={value}
    onChange={onChange}
    onRequest={async (e)=>{
      const attrs = await handleChange(e);
      console.log(attrs);
      onRequest(attrs);
    }}
  />
)
