import React from 'react';
import { uniqueId } from 'underscore';

export default ({ label, className, placeholder, defaultValue="", choices, name = uniqueId('comboBox'), ...rest }) => (
  <div className={className}>
    {label && <label htmlFor={name}>{label}</label>}
    <select id={name} name={name} defaultValue={defaultValue} {...rest} className="form-control" >
      {
        [
          {
            value:"",
            label: placeholder,
            disabled: true,
            selected: true,
          },
          ...choices
        ]
          .map((i,idx) => (
            <option
              key={idx}
              disabled={i.disabled}
              value={i.value}>
              {i.label}
            </option>))
      }
    </select>
  </div>
)
