import React from 'react';
import { uniqueId } from 'underscore';

export default ({ label, className, choices, name = uniqueId('comboBox'), ...rest }) => (
  <div className={className}>
    {label && <label htmlFor={name}>{label}</label>}
    <select id={name} name={name} {...rest} className="form-control" >
      {
        choices.map((i,idx) => (<option selected={i.selected} disabled={i.disabled} value={i.value}>{i.label}</option>))
      }
    </select>
  </div>
)
