import React from 'react';
import cx from 'classnames';
import symbols from './symbols';
import './Currency.css';

export default ({ discount, value = 0, className, currency = 'RUB' }) => (
  <div className={cx('Currency', className)}>
        <span className={cx({ 'Currency-strikethrough': !!discount })}>
          {value / 100}<span className="Currency__symbol">{symbols[currency]}</span>
        </span>
    {
      !!discount &&
      <div className={cx({ 'Currency__discount': !!discount })}>
        <span>{discount.price / 100}<span className="Currency__symbol">{symbols[currency]}</span></span>
      </div>
    }
  </div>
);

export const Price = ({ discount, value = 0, currency = 'RUB', className }) => (
    <div className={cx('Currency', 'Currency--price', className)}>
        <span>
          {!!discount ? discount.price / 100 : value / 100}<span className="Currency__symbol">{symbols[currency]}</span>
        </span>
    </div>
);