import React from 'react';
import cx from 'classnames';
import symbols from './symbols';
import './Currency.css';

export default ({ discount, value = 0, currency = 'RUB' }) => (
  <div className={cx('Currency')}>
        <span className={cx({ 'Currency-strikethrough': !!discount })}>
          {value}<span className="Currency__symbol">{symbols[currency]}</span>
        </span>
    {
      !!discount &&
      <div className={cx({ 'Currency__discount': !!discount })}>
        <span>{discount.price}<span className="Currency__symbol">{symbols[currency]}</span></span>
      </div>
    }
  </div>
);
