import React from 'react';
import cx from 'classnames';
import './DeliveryPeriod.css';

export default ({ min, max, className}) => (
    <div className={cx('DeliveryPeriod', className)}>
      {
        min > 2 &&
        <span>{min} - {max} дней</span>
      }

      {
        min <= 2 &&
        <span>{min} - {max} дня</span>
      }

    </div>
)