import React from 'react';
import cx from 'classnames';
import Button from 'components/Button';
import ButtonToolbar from 'components/ButtonToolbar';
import './DialogToolbar.css';

export default ({onSubmit, onCancel, labelCancel = 'Закрыть', labelOK = 'OK', color = 'dark', className}) => (
    <ButtonToolbar className={cx('DialogToolbar', className)}>
      <Button
          className="DialogToolbar__btn"
          color={color}
          onClick={onCancel}>{labelCancel}</Button>
      <Button
          className="DialogToolbar__btn"
          color={color}
          onClick={onSubmit}>{labelOK}</Button>
    </ButtonToolbar>
)

const SingleButton = ({onCancel, labelOK = 'OK', color = 'dark', className}) => (
    <ButtonToolbar className={cx('DialogToolbar', className)}>
      <Button
          className="DialogToolbar__btn"
          color={color}
          onClick={onCancel}>{labelOK}</Button>
    </ButtonToolbar>
);

export { SingleButton }