import React from 'react';
import './Dictionary.css';
import cx from 'classnames';

export default ({pairs = [], className}) => (
    <dl className={cx('Dictionary', 'row', className)}>
      {
        pairs
        .filter( i => !i.disabled)
        .reduce((acc, i, idx) => (
            [
              ...acc,
              <dt key={`label_${idx}`} className="col-sm-3 col-6">
                {i.label}
              </dt>,
              <dd key={`value_${idx}`} className="col-sm-9 col-6">
                {i.value}
              </dd>,
            ]
        ), [])
      }
    </dl>
)