import React from 'react';
import cx from 'classnames';

export default ({ onClick, src, placeholder="no_photo.jpg", className='', alt='store-image'})=>{
  if (!src) {
    const [name, ext] = placeholder.split('.');
    return <img className={className}
                src={`/img/${name}.${ext}`}
                srcSet={`/img/${name}@2x.${ext} 2x`}
                alt="not_found"
    />;
  }

  const [name, ext] = src.split('.');
  return <img className={cx(className)}
              onClick={onClick}
              src={`/img/${name}.${ext}`}
              // srcSet={`/img/${name}@2x.${ext} 2x`}
              alt={alt}
  />
}
