import React from 'react';
import cx from 'classnames';

export default ({ onClick, src, className='', alt='store-image'})=>{
  if (!src) return <img src="" alt="not_found"/>;

  const [name, ext] = src.split('.');
  return <img className={cx(className)}
              onClick={onClick}
              src={`/img/${name}.${ext}`}
              srcSet={`/img/${name}@2x.${ext} 2x, /img/${name}@3x.${ext} 3x`}
              alt={alt}
  />
}
