import React from 'react';
import cx from 'classnames';
import Sale from 'components/Sale';
import Link from 'components/Link';
import Date from 'components/Date';
import {Price} from 'components/Currency';
import Shipping from 'components/Shipping';
import Dictionary from 'components/Dictionary';
import Address, {parse} from 'components/Address';
import './OrderView.css';

export default ({ className, createdAt, positions, address, discount, delivery, deliveryPrice, productPrice, subtotal, total, help = false })=>(
    <div className={cx('OrderView', className)}>
      {
        createdAt &&
        <div>
          Дата создания: <strong><Date value={createdAt}/></strong>
        </div>
      }

      <table className="table table-bordered OrderView__table">

        <thead>
        <tr>
          <th scope="col">Товар</th>
          <th scope="col">Цена</th>
        </tr>
        </thead>

        <tbody>
        {
          positions.map((i, idx) => (
              <tr key={idx}>
                <td>
                  <Link
                      to={`/product/${i.product.SKU}`}
                      external
                      className="OrderView__name"
                  >
                    {i.product.name} <br/>
                    <small className="text-muted">{i.product.producer}</small>
                  </Link>
                  <span>{` x ${i.amount}`}</span>
                </td>
                <td>
                  <Price value={i.total} className="OrderView__price"/>
                </td>
              </tr>
          ))
        }
        </tbody>
      </table>

      <Dictionary
          className="OrderView__dict"
          pairs={[
            {
              label: 'Цена товара:',
              value: <Price value={productPrice} className="OrderView__price"/>,
            },
            {
              label: 'Скидка:',
              value: <Sale {...discount} className="OrderView__sale"/>,
            },
            {
              label: 'Цена со скидкой:',
              value: <Price value={subtotal} className="OrderView__price"/>,
            },
          ]}
      />
      {
        help &&
        <h6 className="text-muted">
          <small>
            На товары участвующие в распродаже система скидок не
            распространяется, и при совместном заказе с другими товарами их
            стоимость не учитывается в общей сумме заказа для расчета скидки.
          </small>
        </h6>
      }

      <Dictionary
          className="OrderView__dict"
          pairs={[
            {
              label: 'Доставка:',
              value: <Shipping {...delivery}/>,
            },
            {
              label: 'Цена доставки:',
              value: <Price value={deliveryPrice} className="OrderView__price"/>,
            },
            {
              label: 'Итого к оплате:',
              value: <Price value={total} className="OrderView__price"/>,
            },
          ]}
      />

      <div className="OrderView__address">
        <Address {...parse(address)}/>
      </div>
    </div>
)