import React from 'react';
import LazyLoad from 'react-lazyload';
import Sold from 'components/Sold';
import Picture from 'components/Picture';
import Currency from 'components/Currency';
import Placeholder from 'components/Placeholder';
import cx from 'classnames';
import './Product.css';

export default ({ name, pictures = [], producer, price, discount, quantity, className, onClick }) => (
  <div className={cx('Product', className)} onClick={onClick}>
    <LazyLoad once
              width={270}
              height={300}
              offset={100}
              debounce={100}
              placeholder={<Placeholder className="Product__picture" />}>
      <div className="Product__picture_container">
        <Sold disabled={quantity > 0} />
        <Picture src={`/img/${pictures[0]}`} className="Product__picture"/>
      </div>
    </LazyLoad>
    <div className="Product__name">{name}</div>
    <div className="Product__producer">{producer}</div>
    <div className="Product__price">
      <Currency value={price}/>
    </div>
  </div>
)
