import React, { Component } from 'react';
import PropTypes from 'prop-types';
import './Range.css';

const clamp = (val, min, max) => Math.min(Math.max(min, val), max);

export default class Range extends Component {
  handlePlus = () => {
    const { min, max, value} = this.props;
    this.props.onChange(clamp(value + 1, min, max))
  };

  handleMinus = () => {
    const { min, max, value} = this.props;
    this.props.onChange(clamp(value - 1, min, max))
  };

  render() {
    const { min, max, value } = this.props;

    return (
      <div className="Range">
        <div className="Range-minus" onClick={this.handleMinus}>-</div>
        <div className="Range-input">{clamp(value, min, max)}</div>
        <div className="Range-plus" onClick={this.handlePlus}>+</div>
      </div>
    );
  }
}

Range.propTypes = {
  value: PropTypes.number,
  onChange: PropTypes.func,
  max: PropTypes.number,
  min: PropTypes.number,
};
Range.defaultProps = {
  value:0,
  min: 1,
  max: 100,
  onChange: ()=>{},
};
