import React from 'react';
import {kSourceReportStore, kSourceReportGoogle, kSourceReportAdmin} from 'services/localizedSourceReport';

export default ({source, sourceId}) => {
  switch(source) {
    case kSourceReportStore:
      return <span>{'интернет-магазина'}</span>;
    case kSourceReportGoogle:
      return <span>{`${sourceId}`}</span>;
    case kSourceReportAdmin:
      return <span>{'администратор'}</span>;
    default:
      return <span>--</span>
  }
}