import React from 'react';
import MaskedInput from 'react-maskedinput';
import { uniqueId } from 'underscore';

export default ({label, className, name = uniqueId('input'), mask="+7 (111) 111 11 11", type="tel", ...rest})=>(
  <div className={className}>
    {label && <label htmlFor={name}>{label}</label>}
    <MaskedInput {...rest} type={type} id={name} name={name} mask={mask} className="form-control" />
  </div>
)
