import React from 'react';
import {SingleButton} from 'components/DialogToolbar';
import { DialogComponent } from 'modules/Form';

export default class Alert extends DialogComponent {
  render () {
    const { message } = this.props;

    return (
        <div className="Alert">
          <p className="text-center">
            {message}
          </p>
          <SingleButton onCancel={this.onCancel} />
        </div>
    )
  }
}