import React from 'react';
import {DialogComponent} from 'modules/Form';
import OrderComponent from 'components/Order';
import {SingleButton} from 'components/DialogToolbar';
import './OrderView.css';

export default class OrderView extends DialogComponent {
  render() {
    const {value} = this.props;
    return (
        <div className="OrderView">
          <OrderComponent {...value}/>
          <SingleButton onCancel={this.onCancel}/>
        </div>
    );
  }
}