import { Store } from 'modules/Flux';
import Actions from './CartActions';
import XHR, { Methods } from './XHR';

export class Product extends Store {
  constructor() {
    super();

    this.id = 'cart';
    this.listenables = Actions;
  }

  /**
   * Загрузка корзины из сервера
   * @returns {Promise.<void>}
   */
  async onFetch() {
    const { responseData } = await XHR(Methods.POST, { path: 'cart/detail' });
    this.setState(responseData);
  }

  /**
   * Обновить корзину
   * @param body
   * @returns {Promise.<void>}
   */
  async onUpdate(body) {
    const { responseData } = await XHR(Methods.POST, { path: `cart/update`, body });
    this.setState(responseData);
  }

  async onInsert(body) {
    const { responseData } = await XHR(Methods.POST, { path: `cart/insert`, body });
    this.setState(responseData);
  }
}

export default new Product();
