import { Store } from 'modules/Flux';
import Actions from './ProductActions';
import XHR, { Methods } from './XHR';

export class Notation extends Store {
  constructor() {
    super();

    this.id = 'notation';
    this.listenables = Actions;
  }

  /**
   * Загрузка продукт из сервера
   * @param id
   * @returns {Promise.<void>}
   */
  async onFetch(id) {
    try {
      const { responseData } = await XHR(Methods.POST, { path: `catalog/notation/${id}` });
      this.setState(responseData);
    } catch(e) {}
  }
}

export default new Notation();
