import Query from 'modules/Request/Query';

export default {
  /**
   * Список коллекции
   * @returns {Promise.<void>}
   */
  queryForCollections() {
    return Query.POST('/catalog/collections');
  },

  /**
   * Список продуктов
   * @param id
   */
  queryForProducts(id) {
    return Query.POST(`/catalog/products/${id}`);
  },

  /**
   * Список распродаж
   */

  queryForSales() {
    return Query.POST('/catalog/sales');
  }
};

