import Actions from './Actions';

const toMap = (arr) => {
    let map = {};
    for (let i = 0, l = arr.length; i < l; i++) {
      map[arr[i]] = {};
    }
    return map;
};

export const createActions = (cases) => {
    if (Array.isArray(cases)) {
        return Actions.create(toMap(cases));
    }
    return Actions.create(new Map(Object.entries(cases)));
};

