// this needs to be set to true before Keep.js starts storing, done via useKeep
let use = false;

const createdStores = new Map();

function useKeep(bool = true) {
  use = bool;
}

function hasStore(strID) {
  return createdStores.has(strID);
}

function getStore(strID) {
  return createdStores.get(strID);
}

function addStore(strID, str) {
  if (use) {
    createdStores.set(strID, str);
  }
}

function reset() {
  createdStores.clear();
}

export { useKeep, addStore, hasStore, getStore, reset };
