import React from 'react';
import Dialog from 'components/Dialog';

export default ({ dialogKey, dialogs, ...rest }) => {
  const { Component, title, size } = dialogs.paramsDialog(dialogKey);

  return (<Dialog
    showHeader
    closeButton
    size={size}
    header={title}
    show={dialogs.isVisibleDialog(dialogKey)}
    onHide={dialogs.wrapperHideDialog(dialogKey)}
    {...rest}
  >
    {Component}
  </Dialog>);
};
