import React from 'react';
import Dialog from 'components/Dialog';
import Sheet from 'components/Sheet';

export const kTypeDialog = 'dialog';
export const kTypeSheet = 'sheet';

export default ({dialogKey, dialogs, type = kTypeDialog, ...rest}) => {
  const {Component, header, size, showHeader = true, closeButton = true} = dialogs.paramsDialog(
      dialogKey);

  switch (type) {
    case kTypeDialog:
      return (<Dialog
          showHeader={showHeader}
          closeButton={closeButton}
          size={size}
          header={header}
          show={dialogs.isVisibleDialog(dialogKey)}
          onHide={dialogs.wrapperHideDialog(dialogKey)}
          {...rest}
      >
        {Component}
      </Dialog>);
    case kTypeSheet:
      return (<Sheet
          showHeader={showHeader}
          closeButton={closeButton}
          size={size}
          header={header}
          show={dialogs.isVisibleDialog(dialogKey)}
          onHide={dialogs.wrapperHideDialog(dialogKey)}
          {...rest}
      >
        {Component}
      </Sheet>);
    default:
      return null;
  }
};