import React from 'react';
import Dialog from 'components/Dialog';

export default ({ dialogKey, dialogs, showHeader=true, closeButton=true, ...rest }) => {
  const { Component, header, size } = dialogs.paramsDialog(dialogKey);

  return (<Dialog
    showHeader={showHeader}
    closeButton={closeButton}
    size={size}
    header={header}
    show={dialogs.isVisibleDialog(dialogKey)}
    onHide={dialogs.wrapperHideDialog(dialogKey)}
    {...rest}
  >
    {Component}
  </Dialog>);
};
