const transformer = (transform, value) => {
  switch (transform) {
    case 'toNumber':
      return parseInt(value, 10);
    case 'toString':
      return `${value}`;
    case 'toBool':
      return !!value;
    case 'to1r0':
      return !!value >> 0; //eslint-disable-line
    case 'toBinary':
      return value.toString(2);
    case 'toHex':
      return value.toString(16);
    default:
      return value;
  }
};

export default transformer;
