import { delay } from 'underscore';
import Promise from 'bluebird';

function fnDelay(millseconds) {
  return new Promise((resolve) => { delay(resolve, millseconds); });
}

export default {
  seconds(timeEnd) {
    return fnDelay(timeEnd * 1000);
  },
  after(timeEnd) {
    return () => fnDelay(timeEnd * 1000);
  },
};
