import React from 'react';
import cx from 'classnames';
import { times } from 'underscore';
import './PaginationView.css';

export default ({ query, className, minPage = 1 }) => (
  <div className={cx('PaginationView', className)}>
    {
      query.countPage() <= minPage

        ? <div />
        : times(query.countPage(), (i)=><div
        key={i}
        onClick={() => query.loadPage(i, true)}
        className={cx('PaginationView-link',{
          'PaginationView-link-active': query.currentPage() === i,
        })}
      >{i + 1}</div>)
    }
  </div>
)
