import React, { Component } from 'react';
import cx from 'classnames';


export default class ListItem extends Component {
  render() {
    const {className, onClick, style} = this.props;

    return (
        <li
            style={{
              ...style,
              zIndex: 100 - this.props.index
            }}
            onClick={onClick}
            id={this.props.id}
            className={cx(className)}
        >
          <ul>
            {this.props.children}
          </ul>
        </li>
    );
  }
}