import React, { Component } from 'react';
import cx from 'classnames';

export default class RowCell extends Component {
  render() {
    const {style, onClick, className} = this.props;
    return (
        <li style={style}
            onClick={onClick}
            className={cx(className)}>
          {this.props.children}
        </li>
    );
  }
}