export const kDiscountTypePercentage = 0;
export const kDiscountTypeFixedAmount = 1;
export const kDiscountTypeFreeShipping = 2;

export default (type, amount) => {
  switch (type) {
    case kDiscountTypePercentage:
      return `${amount}%`;
    case kDiscountTypeFixedAmount:
      return `${amount} руб.`;
    case kDiscountTypeFreeShipping:
      return `Бесплатная доставка`;
    default:
      return '';
  }
}

const localizeType = (type)=>{
  switch (type) {
    case kDiscountTypePercentage:
      return `Процент`;
    case kDiscountTypeFixedAmount:
      return `Фиксированная`;
    case kDiscountTypeFreeShipping:
      return `Бесплатная доставка`;
    default:
      return '';
  }
};

export { localizeType }