export const kOrderStatusAwaitingPayment = "awaitingPayment";
export const kOrderStatusAwaitingFulfillment = "awaitingFulfillment";
export const kOrderStatusAwaitingPickup = "awaitingPickup";
export const kOrderStatusAwaitingShipment = "awaitingShipment";
export const kOrderStatusShipped = "shipped";
export const kOrderStatusDeclined = "declined";
export const kOrderStatusRefunded = "refunded";


export default (status) => {
  switch (status) {
    case kOrderStatusAwaitingPayment:
      return "Принят. Ожидает оплаты";
    case kOrderStatusShipped:
      return "Завершен";
    case kOrderStatusAwaitingFulfillment:
      return "В обработке";
    case kOrderStatusAwaitingShipment:
      return "Отправлен";
    case kOrderStatusAwaitingPickup:
      return "Сформирован";
    case kOrderStatusDeclined:
      return "Отклонен";
    case kOrderStatusRefunded:
      return "Возрат";
    default:
      return ""
  }
}