export const kOrderStatusAwaitingPayment = 0;
export const kOrderStatusAwaitingFulfillment = 1;
export const kOrderStatusDeclined = 2;
export const kOrderStatusAwaitingShipment = 3;
export const kOrderStatusShipped = 4;
export const kOrderStatusClosed = 5;
export const kOrderStatusRefunded = 6;
export const kOrderStatusCompleted = 7;

export default (status) => {
  switch (status) {
    case kOrderStatusAwaitingPayment:
      return "Принят. Ожидает оплаты";
    case kOrderStatusClosed:
      return "Закрыт";
    case kOrderStatusCompleted:
      return "Выполнен";
    case kOrderStatusShipped:
      return "Готов к выдаче";
    case kOrderStatusAwaitingFulfillment:
      return "В обработке";
    case kOrderStatusAwaitingShipment:
      return "Отправлен";
    case kOrderStatusDeclined:
      return "Отклонен";
    case kOrderStatusRefunded:
      return "Возрат";
    default:
      return ""
  }
}