// Корректирующий отчет
export const kReportTypeFix = "fix";
// Расходная накладная
export const kReportTypeDelivery = "delivery";
// Приходная накладная
export const kReportTypeReceiving = "receiving";

export default (type) => {
  switch (type) {
    case kReportTypeFix:
      return "Исправления";
    case kReportTypeDelivery:
      return "Расход";
    case kReportTypeReceiving:
      return "Приход";
    default: return "";
  }
}
