//Google таблички
export const kSourceReportGoogle = 'google';
//Магазин
export const kSourceReportStore = 'store';
//Ручное создание накладных (возможно потребуется для корекции)
export const kSourceReportAdmin = 'admin';

export default (source) => {
  switch(source) {
    case kSourceReportAdmin:
      return 'Ручное';
    case kSourceReportGoogle:
      return 'Google таблицы';
    case kSourceReportStore:
      return 'Магазин';
    default: return '';
  }
}